<?php

namespace MainBundle\Repository;

/**
 * Commentaire_espaceRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class Commentaire_espaceRepository extends \Doctrine\ORM\EntityRepository
{
    public function findcom($id){
        $q=$this->getEntityManager()
            ->createQuery("select c from MainBundle:Commentaire_espace c WHERE c.espace=:ide")
            ->setParameter(":ide",$id);

        return $q->getResult();
    }
}
